mapper.insert:
INSERT into phh_board(
	user_no
	, title
	, contents
	, count
) VALUES (
	#{userNo}
	, #{title}
	, #{contents}
	, #{count}
)

mapper.update:
UPDATE phh_board 
	SET title= #{title}
	, contents= #{contents}
	, mod_date = NOW()
	WHERE no = #{no}

UPDATE phh_board
	SET count = count + 1
	WHERE no = #{no}

