mapper.insert:
INSERT into syh_board (
	userNum, title, content, count
) VALUES (
	#{userNum}, #{title}, #{content}, #{count}
);

mapper.update:
UPDATE syh_board
SET title = #{title}
	, content = #{content}
	, moddate = NOW()
WHERE num = #{num};

mapper.insert:
INSERT into syh_comments(
	userNum, boardNum, comments
) VALUES (
	#{userNum}, #{boardNum}, #{comments}
);

mapper.update:
UPDATE syh_comments
SET comments = #{comments}
WHERE commentsNum = #{commentsNum}

UPDATE syh_board
	SET count = count + 1
	WHERE num = #{num}

mapper.insert:
INSERT into syh_board (
	userNum, title, content, count, type
) VALUES (
	#{userNum}, #{title}, #{content}, #{count}, #{type}
);

mapper.select:
SELECT * 
FROM syh_board ORDER BY num DESC 
LIMIT #{start}, #{end}
